(809, '_transient_tablepress_a3e84c0c2219d6048320e3abb96ec83d', '\n<table id=\"tablepress-1\" class=\"tablepress tablepress-id-1 table-differences\">\n<thead>\n<tr class=\"row-1\">\n	<th class=\"column-1\">&nbsp;</th><th class=\"column-2\"><img src=\"http://localhost/Sharing/Gatling/base-wordpress/wp-content/uploads/2021/04/Groupe-1547.svg\" alt=\"\" width=\"300\" height=\"300\" class=\"alignnone size-medium wp-image-362\" /></th><th class=\"column-3\"><img src=\"http://localhost/Sharing/Gatling/base-wordpress/wp-content/uploads/2021/04/Groupe-1539.svg\" alt=\"\" width=\"300\" height=\"300\" class=\"alignnone size-medium wp-image-363\" /></th>\n</tr>\n</thead>\n<tbody class=\"row-hover\">\n<tr class=\"row-2\">\n	<td class=\"column-1\"><strong>Scripting</strong></td><td class=\"column-2\"></td><td class=\"column-3\"></td>\n</tr>\n<tr class=\"row-3\">\n	<td class=\"column-1\">Multi-protocols support</td><td class=\"column-2\"><span class=\"check-orange\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-4\">\n	<td class=\"column-1\">Gatling\'s scripting DSL</td><td class=\"column-2\"><span class=\"check-orange\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-5\">\n	<td class=\"column-1\">Web recorder</td><td class=\"column-2\"><span class=\"check-orange\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-6\">\n	<td class=\"column-1\">HAR file import</td><td class=\"column-2\"><span class=\"check-orange\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-7\">\n	<td class=\"column-1\"><strong>Execution</strong></td><td class=\"column-2\"></td><td class=\"column-3\"></td>\n</tr>\n<tr class=\"row-8\">\n	<td class=\"column-1\">Unlimited testing</td><td class=\"column-2\"><span class=\"check-orange\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-9\">\n	<td class=\"column-1\">Unlimited throughput (Virtual Users, Requests per second)</td><td class=\"column-2\"><span class=\"check-orange\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-10\">\n	<td class=\"column-1\">Distributed mode</td><td class=\"column-2\"><span class=\"no-check\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-11\">\n	<td class=\"column-1\"><strong>Reporting</strong></td><td class=\"column-2\"></td><td class=\"column-3\"></td>\n</tr>\n<tr class=\"row-12\">\n	<td class=\"column-1\">Colorful Reports</td><td class=\"column-2\"><span class=\"check-orange\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-13\">\n	<td class=\"column-1\">Response times, Requests and responses per second, Errors</td><td class=\"column-2\"><span class=\"check-orange\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-14\">\n	<td class=\"column-1\">TCP connections, DNS resolutions, Injectors\' monitoring</td><td class=\"column-2\"><span class=\"no-check\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-15\">\n	<td class=\"column-1\">Live reporting</td><td class=\"column-2\"><span class=\"no-check\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-16\">\n	<td class=\"column-1\">History of your runs</td><td class=\"column-2\"><span class=\"no-check\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-17\">\n	<td class=\"column-1\">Trends</td><td class=\"column-2\"><span class=\"no-check\"></span></td><td class=\"column-3\"><span class=\"check-purple\"></span></td>\n</tr>\n<tr class=\"row-18\">\n	<td class=\"column-1\"><strong>Integrations</strong></td><td class=\"column-2\"></td><